mapper.update:
UPDATE member
SET	   pw=#{pw}, addr=#{addr}
WHERE  email=#{email}

mapper.select:
SELECT *
FROM  member 
WHERE 1=1
AND   name=#{name}
AND	  mobile=#{mobile}

